function catanis_vc_add_animation_tab($group = true, $use_animation_default = 'no'){

	$animationTab = array(
		catanis_vc_use_animation(),
		catanis_vc_animation_type(),
		catanis_vc_animation_duration(),
		catanis_vc_animation_delay()
	);

	if(!$group){
		$animationTemp = array();
		foreach ($animationTab as $item){
			unset($item['group']);
			$animationTemp[] = $item;
		}
		$animationTab = $animationTemp;
	}

	if($use_animation_default == 'yes'){
		$animationTemp = array();
		$animationTemp = $animationTab;
		foreach ($animationTab as $key => $item){
			if($item['param_name'] == 'use_animation'){
				$item['std'] = 'yes';
				$item['admin_label'] = false;
				$animationTemp[$key] = $item;
				break;
			}
		}
		$animationTab = $animationTemp;
	}

	return $animationTab;
}

