/*=== Contact Form 7 ========================*/
/*===========================================*/
if ( function_exists('catanis_is_contactForm7_active') && catanis_is_contactForm7_active() ) :
	$cf7 = get_posts( 'post_type="wpcf7_contact_form"&numberposts=-1' );
	$contact_forms = array();
	if ( $cf7 ) {
		foreach ( $cf7 as $cform ) {
			$contact_forms[ $cform->post_title ] = $cform->ID;
		}
